import { call, takeEvery } from 'redux-saga/effects';
import shopActionTypes from './shop.types';
import { fetchCollectionsStarts, fetchCollectionsSuccess, fetchCollectionsFailure } from './shop.actions.js'
import { convertCollectionsSnapshotToMap, getCollectionsRef } from '../../firebase/firebase.utils';

export function* fetchCollectionsAsync() {
    const collectionRef = yield getCollectionsRef('collections');
    const collectionData = yield call(convertCollectionsSnapshotToMap, collectionRef)
}

// dispatch(fetchCollectionsStart())
//         const collectionRef = await('collections');
//         const collectionData = await convertCollectionsSnapshotToMap(collectionRef);
//         dispatch(fetchCollectionsSuccess(collectionData))

export function* fetchCollectionsStart() {
    yield takeEvery(
        shopActionTypes.FETCH_COLLECTIONS_START,
        fetchCollectionsAsync
    )
}
