import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
body {
    font-family: "Open Sans Condensed", sans-serif;
    padding: 20px 80px;
}
a {
    text-decoration: none;
    color: black;
}
a:hover {
    font-weight: bold;
}
* {
    box-sizing: border-box;
}

`
