import { put, takeLatest, all, call } from 'redux-saga/effects'
import UserActionTypes from './user.types'

import { googleProvider, auth, createUserProfileDocument } from '../../firebase/firebase.utils'
import { emailSignInFailure, emailSignInSuccess, googleSignInFailure, googleSignInSuccess } from './user.actions'
import { signInWithEmailAndPassword, signInWithPopup } from 'firebase/auth'

export function* signInWithGoogle() {
    try {
        const { user } = yield signInWithPopup(auth, googleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        yield put(googleSignInSuccess({ id: userRef.id, ...userRef.data() }))
    } catch (error) {
        yield put(googleSignInFailure(error))
    }
}
export function* signInWithEmail({ payload: { auth, email, password } }) {
    try {
        const emailRef = yield call(signInWithEmailAndPassword, (auth, email, password));
        console.log("emailRef", emailRef);
        yield put(emailSignInSuccess(emailRef));
    } catch (error) {
        yield put(emailSignInFailure(error))
    }
}


export function* onGoogleSignInStart() {
    yield takeLatest(
        UserActionTypes.GOOGLE_SIGN_IN_START,
        signInWithGoogle
    )
}
export function* onEmailSignInStart() {
    yield takeLatest(
        UserActionTypes.EMAIL_SIGN_IN_START,
        signInWithEmail
    )
}

export function* userSagas() {
    yield all([
        call(onGoogleSignInStart),
        call(onEmailSignInStart),
    ])
}
