import { collection, doc, getDocs, onSnapshot } from 'firebase/firestore';
import React from 'react';
import { connect } from 'react-redux';
import { Route, Routes } from 'react-router-dom';
import { createStructuredSelector } from 'reselect';
import CollectionOverview from '../../components/collections-overview/collections-overview.component';
import { convertCollectionsSnapshotToMap, firestore, getCollectionsRef } from '../../firebase/firebase.utils';
import { fetchCollectionsStartAsync, updateCollections } from '../../redux/shop/shop.actions';
import { selectIsCollectionFetching } from '../../redux/shop/shop.selectors';
import CollectionPage from '../collection/collection.component';
import './shop.style.scss';

class ShopPage extends React.Component {
    // state = {
    //     loading: true
    // }
    // unSubscribeFromSnapshot = null;

    // async componentDidMount() {
    //     let collectionRef = await getCollectionsRef('collections');
    //     let collectionData = await convertCollectionsSnapshotToMap(collectionRef);
    //     this.props.updateCollections(collectionData)
    //     if (this.props.collections) {
    //         this.setState({ loading: false })
    //     }
    // }
    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync()
    }
    render() {
        const { isCollectionFetching } = this.props;
        console.log("isCollectionFetching", isCollectionFetching);
        return (
            <div className="shop-page" >
                <Routes>
                    <Route path='/' element={!isCollectionFetching ? <CollectionOverview /> : <h1>Loading</h1>} />
                    <Route path=':collectionId' element={!isCollectionFetching ? <CollectionPage /> : <h1>Loading</h1>} />
                </Routes>
            </div>
        )
    }
}
const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectIsCollectionFetching
})
const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})


export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
