import { convertCollectionsSnapshotToMap, getCollectionsRef } from "../../firebase/firebase.utils";
import shopActionTypes from "./shop.types";

export const fetchCollectionsStart = () => ({
    type: shopActionTypes.FETCH_COLLECTIONS_START
})

export const fetchCollectionsStartAsync = () => {
    return (dispatch) => {
        let collectionRef = getCollectionsRef('collections');

        dispatch(fetchCollectionsStart())
        let collectionData = convertCollectionsSnapshotToMap(collectionRef);
        this.props.updateCollections(collectionData)
        if (this.props.collections) {
            this.setState({ loading: false })
        }
    }
}
export const fetchCollectionsSuccess = (collection) => ({
    type: shopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload: collection
})
export const fetchCollectionsFailure = (errorMessage) => ({
    type: shopActionTypes.FETCH_COLLECTIONS_FAILURE,
    payload: errorMessage
})
